INSERT INTO `modx_site_htmlsnippets` VALUES ('56','amp-Template-css','inline styles for amp-template','2','none','16','0','/* Generic styling */\n.alignright { float: right; }\n.alignleft { float: left; }\n.aligncenter { display: block; margin-left: auto; margin-right: auto; }\n.clear { clear: both; }\n\n\n/* Template Styles */\n.ampx-content, .amp-wp-title-bar div {\n	max-width: 900px;\n	margin: 0 auto;\n}\n\nbody {\n	font-family: \'Merriweather\', Serif;\n	font-size: 16px;\n	line-height: 1.8;\n	background: #fff;\n	color: #3d596d;\n	padding-bottom: 0px;\n}\n\n.ampx-content {\n	padding: 16px;\n	overflow-wrap: break-word;\n	word-wrap: break-word;\n	font-weight: 400;\n	color: #3d596d;\n}\n\n.ampx-title {\n	margin: 36px 0 0 0;\n	font-size: 36px;\n	line-height: 1.258;\n	font-weight: 700;\n	color: #2e4453;\n}\n.ampx-title a{\ntext-decoration:none;\n}\n\n.ampx-meta {\n	margin: 10px 0 16px 0;\n}\n.ampx-img-blog {\n  padding: 4px;\n  background-color: #fff;\n  border: 1px solid #ddd;\n  border-radius: 4px;\n}\n.ampx-item-blog {\n  padding: 4px;\n  background-color: #fff;\n  border: 1px solid #ddd;\n  border-radius: 4px;\n  clear:both;\nmargin-bottom: 20px;\n}\np,\nol,\nul,\nfigure {\n	margin: 0 0 24px 0;\n}\n\na,\na:visited {\n	color: #0087be;\n}\n\na:hover,\na:active,\na:focus {\n	color: #33bbe3;\n}\n\n.ampx-footer {\n	background: #e9eff3;\n	padding: 20px 16px;\ntext-align:center\n}\n.ampx-footer a {color: #499bea;	text-decoration: none;}\n.ampx-footer a:hover{color: #3d596d;text-decoration: none;}\n\nnav.ampx-title-bar {\n	width:100%;\n	position: fixed;\n    top: 0;\n    z-index: 10;\n    background: #f2f2f2;\n    box-shadow: inset 0px -1px 0px white, inset 0px 0px 0px rgba(255, 255, 255, 0.4), 0px 0px 0px rgba(0, 0, 0, 0.8);\n    -moz-box-shadow: inset 0px -1px 0px white, inset 0px 0px 0px rgba(255, 255, 255, 0.4), 0px 0px 0px rgba(0, 0, 0, 0.8);\n    -webkit-box-shadow: inset 0px -1px 0px white, inset 0px 0px 0px rgba(255, 255, 255, 0.4), 0px 0px 0px rgba(0, 0, 0, 0.8);\n    border-bottom: 1px solid #dfdfdf;\n    text-transform: uppercase;\n    font-size: 14px;\n    clear: both;\n}\n\nnav.ampx-title-bar div {\n	line-height: 54px;\n	color: #fff;\n}\n\nnav.ampx-title-bar a {\n	color: #fff;\n	text-decoration: none;\n}\n\nnav.ampx-title-bar .ampx-site-icon {\n	/** site icon is 32px **/\n 	float: left;\n	margin: 12px 8px 0 0;\n	border-radius: 50%;\n}\n\n.ampx-meta {\n	background: #e9eff3;\n	padding: 5px;\n	color: #4f748e;\n}\n.ampx-meta a {\n	color: #4f748e;\n}\n\n.togglesidebar\n{background: transparent;padding-top:8px; max-height:36px;}\n.togglesidebar amp-img {cursor: pointer;}\n\n.amp-close-image {background: #499bea;padding-top:4px;cursor: pointer;}\n\n/* Quotes */\nblockquote {\n	padding: 16px;\n	margin: 8px 0 24px 0;\n	border-left: 2px solid #87a6bc;\n	color: #4f748e;\n	background: #e9eff3;\n}\n\nblockquote p:last-child {\n	margin-bottom: 0;\n\n}\n\n/* Other Elements */\namp-carousel {\n	background: #000;\n}\n\namp-iframe,\namp-youtube,\namp-instagram,\namp-vine {\n	background: #f3f6f8;\n}\n\namp-carousel > amp-img > img {\n	object-fit: contain;\n}\n\n.ampx-iframe-placeholder {\n	background: #f3f6f8 url( ) no-repeat center 40%;\n	background-size: 48px 48px;\n	min-height: 48px;\n}\n/* pagination */\na.amp_page {\n    border: 1px solid #008CBA;\n    padding: 1px 7px 2px; \n    margin-right: 1px;\n    color: #008CBA;\n} \n.amp_currentpage {\n    border: 1px solid #ccc;\n    padding: 1px 8px 2px; \n    margin-right: 1px;\n    color: #ccc;\n}\na.amp_page:hover {\n    border: 1px solid #008CBA;\n    background-color: #008CBA;\n    color: #fff;\n}\n/*button*/\n.button {\n  background: #007c49;\n  -webkit-border-radius: 4;\n  -moz-border-radius: 4;\n  border-radius: 4px;\n  font-family: Arial;\n  color: #ffffff;\n  font-size: 20px;\n  padding: 10px 20px 10px 20px;\n  text-decoration: none;\n}\n\n.button:hover {\n  background: #03a763;\n  text-decoration: none;\n}\na.button {color: #FFF;}','0');
