INSERT INTO `modx_site_snippets` VALUES ('14','Reflect','<strong>2.2</strong> Generates date-based archives using Ditto','0','7','0','\n/**\n * Reflect\n * \n * Generates date-based archives using Ditto\n *\n * @category 	snippet\n * @version 	2.2\n * @license 	http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @internal	@properties\n * @internal	@modx_category Content\n * @internal    @installset base, sample\n * @documentation Cheatsheet https://de.scribd.com/doc/55919355/MODx-Ditto-and-Reflect-Cheatsheet-v1-2\n * @documentation Inside snippet-code\n * @reportissues https://github.com/modxcms/evolution\n * @author      Mark Kaplan\n * @author      Ryan Thrash http://thrash.me\n * @author      netProphET, Dmi3yy, bossloper, yamamoto\n * @lastupdate  2016-11-21\n */\n\n/*\n *  Note: \n *  If Reflect is not retrieving its own documents, make sure that the\n *  Ditto call feeding it has all of the fields in it that you plan on\n *  calling in your Reflect template. Furthermore, Reflect will ONLY\n *  show what is currently in the Ditto result set.\n *  Thus, if pagination is on it will ONLY show that page\'s items.\n*/\n\nreturn require MODX_BASE_PATH.\'assets/snippets/reflect/snippet.reflect.php\';\n','0','','');
INSERT INTO `modx_site_snippets` VALUES ('22','ampPagination old','ditto pagination for amp','0','16','0','if(!defined(\'MODX_BASE_PATH\')){die(\'What are you doing? Get out of here!\');}\n/* \nBased on splitPagination by Crossland\ne.g. 1 \n\n[[ampPagination]]\nwill just create a placeholder named \"ampPages\" - to insert the pagination bar into the correct place of your document use the [+ampPages+] placeholder\n\ne.g.2\n[[ampPagination? &id=`myDitto` &pagesToShow=`10` &constantEndCount=`3` &ellipses=`myChunk` &return=`1`]]\n*/\n\n\n\nif (!function_exists(\"generatePagination\")) {\n	\nfunction generatePagination($currentPage, $totalResults, $resultsPerPage, $pagesToShow, $constantEndCount) {\n	\n	// How many pages are there altogether?\n	$totalPages = ceil($totalResults / $resultsPerPage);\n	\n	// How far through are we?\n	$pagesBefore = $currentPage - 1;\n	$pagesAfter = $totalPages - $currentPage;\n	\n	// An array of tabs to display (made up of page numbers)\n	$tabArr = array();\n	\n	// If we need to split the pagination\n	if($totalPages > $pagesToShow) {\n		\n		// If we\'re currently more than half way through\n		if($pagesBefore > $pagesToShow/2) {\n			\n			// Always show pages 1 and 2 (or whatever the constant end count is), then a separator\n			for($i=1; $i<=$constantEndCount; $i++) { $tabArr[] = $i; }\n			$tabArr[] = 0;\n				\n			// If the number of pages after the current page is more than the number of available slots\n			if($pagesAfter >= $pagesToShow/2) {\n				$empty_slots_before = floor( ($pagesToShow - ($constantEndCount*2 /*there are two ends*/) - 1 /*the current page*/) / 2 /*only half*/) -1  /*the set of dots counts as one slot*/;\n			} else {\n				$empty_slots_before = $pagesToShow - count($tabArr) - 1 /*current page*/ - $pagesAfter /*How many after*/;\n			}\n			\n			// Insert the tabs	\n			for($i=($currentPage-$empty_slots_before); $i<$currentPage; $i++) { $tabArr[] = $i; }	\n			\n		} else {\n			for($i=1; $i<$currentPage; $i++) { $tabArr[] = $i; }\n		}\n		\n		// Insert current page\n		$tabArr[] = $currentPage;		\n		\n		// If we\'re less than half way through\n		if($pagesAfter > $pagesToShow/2) {\n			\n			// How many more slots do we want to show after the current page?\n			$empty_slots_after = $pagesToShow - count($tabArr) - $constantEndCount - 1/*the set of dots*/;\n			\n			// Insert the tabs	\n			for($i=($currentPage+1); $i<=$currentPage+$empty_slots_after; $i++) { $tabArr[] = $i; }\n			\n			// Add a separator\n			$tabArr[] = 0;\n			\n			// Always show pages last 2 pages (or whatever the constant end count is)\n			for($i=$totalPages-$constantEndCount+1; $i<=$totalPages; $i++) { $tabArr[] = $i; }\n\n		} else {\n			for($i=($currentPage+1); $i<=$totalPages; $i++) { $tabArr[] = $i; }\n		}\n		\n	} else { // No need to split the pagination\n		for($i=1;$i<=$totalPages;$i++) { $tabArr[] = $i; }\n	}\n			\n	return $tabArr;\n	\n	} // end function\n} // end if\n\n\n\n\n// ----------------------------------------------------------------------------------------------------------------\n\n// Define some paramaters\n$id = isset($id) ? $id.\'_\' : \'\';\n$total = isset($total) ? $total : $modx->getPlaceholder($id.\"total\");\n$start = isset($start) ? $start : $modx->getPlaceholder($id.\"start\");\n$display = isset($display) ? $display : $modx->getPlaceholder($id.\"perPage\");\n$currentPage = isset($currentPage) ? $currentPage : $modx->getPlaceholder($id.\"current\");\n$landing = isset($tagDocumentID) ? $tagDocumentID : $modx->documentObject[\'id\'];\n\n$pagesToShow = isset($pagesToShow) && is_numeric($pagesToShow) ? $pagesToShow : 15;\n$constantEndCount = isset($constantEndCount) && is_numeric($constantEndCount) ? $constantEndCount : 2;\n\n\n$tplEllipses = isset($tplEllipses) ? $modx->getChunk($tplEllipses) : \'\';\n$tplEllipses = !empty($tplEllipses) ? $tplEllipses : \'<span class=\"ampPagination\">...</span>\';\n\n$tplCurrent = isset($tplCurrent) ? $modx->getChunk($tplCurrent) : \'\';\n$tplCurrent = !empty($tplCurrent) ? $tplCurrent : \'<span class=\"amp_currentpage amp_page_[+page+][+class+]\">[+page+]</span>\';\n\n$tplPageLink = isset($tplPageLink) ? $modx->getChunk($tplPageLink) : \'\';\n$tplPageLink = !empty($tplPageLink) ? $tplPageLink : \'<a class=\"amp_page amp_page_[+page+][+class+]\" href=\"[+url+]\">[+page+]</a>\';\n\n$firstClass = isset($firstClass) ? $firstClass: \'first\';\n$lastClass = isset($lastClass) ? $lastClass: \'last\';\n\n\n$return = isset($return) ? $return : 0;\n\n// Do nothing if there\'s nothing to do\nif ($total == 0 || $display==0 || $total <= $display) {\n	return false;\n}\n\n// What page are we on?\n$page = ceil($start/$display);\n\n// Generate the pagination\n$paginationArray = generatePagination($page, $total, $display, $pagesToShow, $constantEndCount);\n\n// Create empty placeholder for output\n$ph = \"\";\n\n// Build the output\nforeach($paginationArray as $page) {\n	$inc = ($page-1)*$display;\n	\n	// First or last classes\n	if ($page == 1) { \n			$newClass = \' \' . $firstClass;\n	} else if ($page == ceil($total/$display)) {\n		$newClass = \' \' . $lastClass;\n	} else {\n		$newClass = \'\';\n	}\n	\n	// Insert into output\n	if($page == 0) {\n		$ph .= $tplEllipses; // print an elipse, representing pages that aren\'t displayed\n	} else if ($inc==$_GET[$id.\'start\']) {\n		$newLink = str_replace(\'[+class+]\', $newClass , $tplCurrent);\n		$ph .= str_replace(\'[+page+]\', $page, $newLink);	\n	} else {\n		$newLink = str_replace(\'[+url+]\', ditto::buildURL(\"start=\".$inc,$landing,$id) , $tplPageLink);		\n		$newLink = str_replace(\'[+class+]\', $newClass , $newLink);\n		$ph .= str_replace(\'[+page+]\', $page, $newLink);	\n	}\n}\n\n// Set the placeholder\n$modx->setPlaceholder($id.\"ampPages\",$ph);\n\n// Or return the output\nif ($return) return $ph;\n','0','','');
INSERT INTO `modx_site_snippets` VALUES ('23','amptv old','Output Tv values to amp template','0','16','0','/**\n* amptv snippet: Output Tv values to amp template\n[[amptv? &tv=`download`]]\noutput tv using modx tv widget output\n[[amptv? &tv=`download` &widget=`1`]]\n**/\nif (isset($_GET[\'ampid\'])){\n$id=$modx->db->escape($_GET[\'ampid\']);\n$widget = (isset($widget)) ? $widget : 0;\n$output = \'\';\nif(isset($widget) && 1==$widget){\n        $getTv = $modx->getTemplateVarOutput($tv,$id);\n        $output = $getTv[$tv];\n    }else{\n        $getTv = $modx->getTemplateVar($tv,\'*\',$id);\n        $output = ($getTv[\'value\']!=\'\') ? $getTv[\'value\'] : $getTv[\'defaultText\'];\n    }\n/**out**/\nreturn $output;\n}\n','0','','');
INSERT INTO `modx_site_snippets` VALUES ('24','ampx old','Accelerated Mobile Pages for Evo - Beta 1.0.7','0','16','0','\n/* add to the head tag of your template (where 57 is the resource containing ampx snippet call)\n * <link rel=\"amphtml\" href=\"[(site_url)][~57~]?ampid=[*id*]\" />\n *  you can pass tpl parameter via URL:\n *  <link rel=\"amphtml\" href=\"[(site_url)][~57~]?ampid=[*id*]&tpl=amp-Template-Home\" />\n*/	\n$tpl = isset($_GET[\'tpl\']) ? $_GET[\'tpl\'] : \'amp-Template\';\n$imageTv = isset($imageTv) ? $imageTv : \'Thumbnail\';\n$noimage = isset($noimage) ? $noimage : \'assets/snippets/ampx/images/noimage.png\';\n\nDEFINE(\'AMP__DIR__\', \'assets/snippets/ampx/\');\nrequire_once( AMP__DIR__ . \'includes/class-amp-post-template.php\' );\nrequire_once( AMP__DIR__ . \'simple_html_dom.php\' );\n$cleancontent =\'<h1> ampx Error</h1><p>No document was given to generate AMP html page.<br/> Missing value for ampid parameter</p>\';\n\n\n$data_query = $modx->db->select(\"id\", $modx->getFullTableName(\'site_content\'),  \"alias=\'\".$_GET[\'alias\'].\"\'\");  \nwhile ($data = mysqli_fetch_array($data_query))\n{\n  $id = $data[\"id\"];\n}\n\nif (isset($_GET[\'alias\'])){\n\n	$parentid = $modx->getParent($id,1,\'id\');\n\n	//get modx resource placeholder \n$title = $modx->getPageInfo($id,1,\'pagetitle\'); \n$longtitle = $modx->getPageInfo($id,1,\'longtitle\'); \n$menutitle = $modx->getPageInfo($id,1,\'menutitle\');\n$description = $modx->getPageInfo($id,1,\'description\'); \n$introtext = $modx->getPageInfo($id,1,\'introtext\'); \n	\n//get content, sanitize images and strip html tags\n$sourcecontent = $modx->getPageInfo($id,1,\'content\'); \n$ampContent = new AMP_Content($sourcecontent[\'content\'], array(), array(\'AMP_Img_Sanitizer\' => array()), array());\n$content = $ampContent->get_amp_content();\n$strip_tagscontent = strip_tags($content, \'<p><a><h1><h2><h3><h4><h5><b><strong><ol><ul><li><code><pre><amp-img><table><tr><td>\');\n//strip html attributes (timymce editor)\nfunction stripAttributes($strip_tagscontent,$attribs) {\n    $dom = new simple_html_dom();\n    $dom->load($strip_tagscontent);\n    foreach($attribs as $attrib)\n        foreach($dom->find(\"*[$attrib]\") as $e)\n            $e->$attrib = null; \n    $dom->load($dom->save());\n    return $dom->save();\n}\n$attribs = array(\n	\"style\" => \"style\",\n	\"class\" => \"class\",\n	\"align\" => \"align\",\n);\n$cleancontent = stripAttributes($strip_tagscontent,$attribs);\n\n//get datePublished from publishedon\n//setlocale ( LC_ALL , \"it_IT\" ); \n$publishedon = $modx->getPageInfo($id,1,\'publishedon\'); \n$datePublished = date(r,$publishedon[\'publishedon\']);\n//get dateModified from editedon\n$editedon = $modx->getPageInfo($id,1,\'editedon\'); \n$dateModified = date(r,$editedon[\'editedon\']);\n\n//get canonical url from page\n$canonicalurl = $modx->makeUrl($id, \'\', \'\', \'full\');	\n\n//get image tv\n$getimage = $modx->getTemplateVarOutput($imageTv,$id);\n$imagetvar = $getimage[$imageTv];\nif (empty($imagetvar)) {\n   $mainimage = $noimage; //default image if tv is empty\n}\n	else {\n$mainimage = $imagetvar;\n	}\n\n//get author\n$createdby = $modx->getPageInfo($id,1,\'createdby\'); \n$user_info = $modx->getUserInfo($createdby[\'createdby\']);\n$author = $user_info[\'fullname\'];\n\n}\n\n//parse chunk placeholders\n$values = array(\'ampid\' => $id, \'parent\' => $parentid[\'id\'], \'canonicalurl\' => $canonicalurl, \'ampcontent\' => $cleancontent, \'amplongtitle\' => $longtitle[\'longtitle\'], \'amptitle\' => $title[\'pagetitle\'], \'ampmenutitle\' => $menutitle[\'menutitle\'], \'ampdescription\' => $description[\'description\'], \'ampintrotext\' => $introtext[\'introtext\'],\'ampimage\' => $mainimage, \'datePublished\' => $datePublished, \'dateModified\' => $dateModified, \'author\' => $author);\n$output =  $output . $modx->parseChunk($tpl, $values, \'[+\', \'+]\');\n//var_dump($title);\nreturn $output;','0','{}',' ');
INSERT INTO `modx_site_snippets` VALUES ('25','ampPagination','ditto pagination for amp','0','16','0','if(!defined(\'MODX_BASE_PATH\')){die(\'What are you doing? Get out of here!\');}\n/* \nBased on splitPagination by Crossland\ne.g. 1 \n\n[[ampPagination]]\nwill just create a placeholder named \"ampPages\" - to insert the pagination bar into the correct place of your document use the [+ampPages+] placeholder\n\ne.g.2\n[[ampPagination? &id=`myDitto` &pagesToShow=`10` &constantEndCount=`3` &ellipses=`myChunk` &return=`1`]]\n*/\n\n\n\nif (!function_exists(\"generatePagination\")) {\n	\nfunction generatePagination($currentPage, $totalResults, $resultsPerPage, $pagesToShow, $constantEndCount) {\n	\n	// How many pages are there altogether?\n	$totalPages = ceil($totalResults / $resultsPerPage);\n	\n	// How far through are we?\n	$pagesBefore = $currentPage - 1;\n	$pagesAfter = $totalPages - $currentPage;\n	\n	// An array of tabs to display (made up of page numbers)\n	$tabArr = array();\n	\n	// If we need to split the pagination\n	if($totalPages > $pagesToShow) {\n		\n		// If we\'re currently more than half way through\n		if($pagesBefore > $pagesToShow/2) {\n			\n			// Always show pages 1 and 2 (or whatever the constant end count is), then a separator\n			for($i=1; $i<=$constantEndCount; $i++) { $tabArr[] = $i; }\n			$tabArr[] = 0;\n				\n			// If the number of pages after the current page is more than the number of available slots\n			if($pagesAfter >= $pagesToShow/2) {\n				$empty_slots_before = floor( ($pagesToShow - ($constantEndCount*2 /*there are two ends*/) - 1 /*the current page*/) / 2 /*only half*/) -1  /*the set of dots counts as one slot*/;\n			} else {\n				$empty_slots_before = $pagesToShow - count($tabArr) - 1 /*current page*/ - $pagesAfter /*How many after*/;\n			}\n			\n			// Insert the tabs	\n			for($i=($currentPage-$empty_slots_before); $i<$currentPage; $i++) { $tabArr[] = $i; }	\n			\n		} else {\n			for($i=1; $i<$currentPage; $i++) { $tabArr[] = $i; }\n		}\n		\n		// Insert current page\n		$tabArr[] = $currentPage;		\n		\n		// If we\'re less than half way through\n		if($pagesAfter > $pagesToShow/2) {\n			\n			// How many more slots do we want to show after the current page?\n			$empty_slots_after = $pagesToShow - count($tabArr) - $constantEndCount - 1/*the set of dots*/;\n			\n			// Insert the tabs	\n			for($i=($currentPage+1); $i<=$currentPage+$empty_slots_after; $i++) { $tabArr[] = $i; }\n			\n			// Add a separator\n			$tabArr[] = 0;\n			\n			// Always show pages last 2 pages (or whatever the constant end count is)\n			for($i=$totalPages-$constantEndCount+1; $i<=$totalPages; $i++) { $tabArr[] = $i; }\n\n		} else {\n			for($i=($currentPage+1); $i<=$totalPages; $i++) { $tabArr[] = $i; }\n		}\n		\n	} else { // No need to split the pagination\n		for($i=1;$i<=$totalPages;$i++) { $tabArr[] = $i; }\n	}\n			\n	return $tabArr;\n	\n	} // end function\n} // end if\n\n\n\n\n// ----------------------------------------------------------------------------------------------------------------\n\n// Define some paramaters\n$id = isset($id) ? $id.\'_\' : \'\';\n$total = isset($total) ? $total : $modx->getPlaceholder($id.\"total\");\n$start = isset($start) ? $start : $modx->getPlaceholder($id.\"start\");\n$display = isset($display) ? $display : $modx->getPlaceholder($id.\"perPage\");\n$currentPage = isset($currentPage) ? $currentPage : $modx->getPlaceholder($id.\"current\");\n$landing = isset($tagDocumentID) ? $tagDocumentID : $modx->documentObject[\'id\'];\n\n$pagesToShow = isset($pagesToShow) && is_numeric($pagesToShow) ? $pagesToShow : 15;\n$constantEndCount = isset($constantEndCount) && is_numeric($constantEndCount) ? $constantEndCount : 2;\n\n\n$tplEllipses = isset($tplEllipses) ? $modx->getChunk($tplEllipses) : \'\';\n$tplEllipses = !empty($tplEllipses) ? $tplEllipses : \'<span class=\"ampPagination\">...</span>\';\n\n$tplCurrent = isset($tplCurrent) ? $modx->getChunk($tplCurrent) : \'\';\n$tplCurrent = !empty($tplCurrent) ? $tplCurrent : \'<span class=\"amp_currentpage amp_page_[+page+][+class+]\">[+page+]</span>\';\n\n$tplPageLink = isset($tplPageLink) ? $modx->getChunk($tplPageLink) : \'\';\n$tplPageLink = !empty($tplPageLink) ? $tplPageLink : \'<a class=\"amp_page amp_page_[+page+][+class+]\" href=\"[+url+]\">[+page+]</a>\';\n\n$firstClass = isset($firstClass) ? $firstClass: \'first\';\n$lastClass = isset($lastClass) ? $lastClass: \'last\';\n\n\n$return = isset($return) ? $return : 0;\n\n// Do nothing if there\'s nothing to do\nif ($total == 0 || $display==0 || $total <= $display) {\n	return false;\n}\n\n// What page are we on?\n$page = ceil($start/$display);\n\n// Generate the pagination\n$paginationArray = generatePagination($page, $total, $display, $pagesToShow, $constantEndCount);\n\n// Create empty placeholder for output\n$ph = \"\";\n\n// Build the output\nforeach($paginationArray as $page) {\n	$inc = ($page-1)*$display;\n	\n	// First or last classes\n	if ($page == 1) { \n			$newClass = \' \' . $firstClass;\n	} else if ($page == ceil($total/$display)) {\n		$newClass = \' \' . $lastClass;\n	} else {\n		$newClass = \'\';\n	}\n	\n	// Insert into output\n	if($page == 0) {\n		$ph .= $tplEllipses; // print an elipse, representing pages that aren\'t displayed\n	} else if ($inc==$_GET[$id.\'start\']) {\n		$newLink = str_replace(\'[+class+]\', $newClass , $tplCurrent);\n		$ph .= str_replace(\'[+page+]\', $page, $newLink);	\n	} else {\n		$newLink = str_replace(\'[+url+]\', ditto::buildURL(\"start=\".$inc,$landing,$id) , $tplPageLink);		\n		$newLink = str_replace(\'[+class+]\', $newClass , $newLink);\n		$ph .= str_replace(\'[+page+]\', $page, $newLink);	\n	}\n}\n\n// Set the placeholder\n$modx->setPlaceholder($id.\"ampPages\",$ph);\n\n// Or return the output\nif ($return) return $ph;\n','0','','');
