(12, 'SubStr', 'Обрезает текст до нужно величины', 0, 10, 0, '\r\n$size= isset($size)? $size:''200'';\r\n$docid = (isset($docid)) ? $docid : $modx->documentIdentifier;\r\n$field = isset($field)? $field:''content'';\r\n\r\n$docFields = $modx->getDocument($docid);\r\n\r\n$text = $docFields[$field];\r\n$text = strip_tags($text);\r\n$text = mb_substr($text,0,$size);\r\n\r\n$text = explode('' '',$text);\r\narray_pop($text);\r\n$text = implode('' '',$text);\r\nreturn $text.''<span>...</span>'';\r\n', 0, '', ' '),
(13, 'MetaDesc', 'Обрезает текст до нужно величины', 0, 10, 0, '\r\n$desc = $modx->getTemplateVar(''desc'',''*'',$docid);\r\n$intr = $modx->getTemplateVar(''introtext'',''*'',$docid);\r\n\r\nif ((!empty($desc[''value'']) && $desc[''value''] != ''[*introtext*]'') || \r\n	(!empty($intr[''value'']) && $desc[''value''] == ''[*introtext*]'')) {\r\n	\r\n	return $desc[''value''];\r\n	\r\n} else {\r\n	\r\n	$size= isset($size)? $size:''200'';\r\n	$docid = (isset($docid)) ? $docid : $modx->documentIdentifier;\r\n	$field = isset($field)? $field:''content'';\r\n	\r\n	$docFields = $modx->getDocument($docid);\r\n	\r\n	$text = $docFields[$field];\r\n	$text = strip_tags($text);\r\n	$text = mb_substr($text,0,$size);\r\n	\r\n	$text = explode('' '',$text);\r\n	array_pop($text);\r\n	$text = implode('' '',$text);\r\n	\r\n	return $text;\r\n\r\n}\r\n', 0, '', ' '),
